import React from 'react'
// import styles from './OrderItems.module.scss'
import AppContext from '../../context'
import {useContext} from 'react'

const OrderItems = () => {
    const {cartItems} = useContext(AppContext)
    console.log(cartItems)
  return (
    <div className="items ">
        cartItems.map
            {cartItems.length>0?<div className="item d-flex justify-center align-center">
            <img width={128} height={128} src={cartItems[0].image} alt='image'></img>
            <span>{cartItems[0].description}</span>
            <span>{cartItems[0].price}</span>
            <button></button>
        </div>:<h2>You should order something</h2>}
    </div>
  )
}

export default OrderItems
