import React from 'react'
import {useState, useContext} from 'react'
import styles from "../DeliveryForm/DeliveryForm.module.scss"
import axios from 'axios'
import AppContext from '../../context'
import {Link} from "react-router-dom"


const DeliveryForm = () => {
    const {cartItems} = useContext(AppContext) 
    // const [name, setName] = useState("")
    // const [city, setCity] = useState("")
    // const [adress, setAdress] = useState("")
    // const [phone, setPhone] = useState("")
    const [formValue, setFormValue] = useState(initialState)
    const [errorName, setErrorName] = useState(null)
    const [errorCity, setErrorCity] = useState(null)
    const [errorPhone, setErrorPhone] = useState(null)
    const [errorAdress, setErrorAdress] = useState(null)
    const {name, city, adress, phone} = formValue;
   
    

    const postData = async(obj)=>{
        try{
            await axios.post("https://62f615b0612c13062b45e6f7.mockapi.io/personData", obj)
        }
        catch(err){
            console.log(err.message)
        }
    }

    const onNameChange =(e)=>{
        setErrorName(null)
        const {name, value} = e.target.value
        setFormValue({...formValue,[name]:value})
        
    }
    // const onInputChange = (e)=>{
    //     // console.log(e.target.name, e.target.value)
    //     const {name, value} = e.target
    //     setFormValue({...formValue, [name]:value})
    //     if(formValue.name.length===0){
    //         console.log(formValue.name)
    //     }
    // }



    

    const getDataFromInput = (e) =>{
        e.preventDefault()
        
        
        // const {name, city, adress, phone} = formValue;
        const obj = {...formValue, items:cartItems}
        console.log(obj)
        // console.log(errorName)
        // if(!errorName && !errorCity && !errorAdress && !errorPhone){
            postData(obj)
        // }
        
        //Your order is accepted

    }
    
  return (
    <form onSubmit={getDataFromInput}>
        <div className={styles.inputs}>
            <div className={styles.input}>
                <p>Name</p>   
                {errorName? <input name="name" value = {name} placeholder="Name input must be filled" onChange={onNameChange} style={{borderColor: "#FF0000"}}></input>:
                 <input name="name" placeholder="Enter your name..." onChange={onNameChange}></input>
                }
                
            </div>
            <div className={styles.input}>
                <p>City</p>   
                <input name="city" placeholder="Enter your city..." ></input>
            </div>
            <div className={styles.input}>
                <p>Adress</p>   
                <input name="adress" placeholder="Enter your adress..." ></input>
            </div>
            <div className={styles.input}>
                <p>Phone</p>   
                <input name="phone" placeholder="Enter your phone..."></input>
            </div>
            {/* <Link to="/accept"> */}
                <button className={styles.submit} type="submit">Submit</button>
            {/* </Link> */}
            
        </div>
    </form>
    
  )
}

export default DeliveryForm
