import React from 'react'
import {useState, useContext} from 'react'
import styles from "../DeliveryForm/DeliveryForm.module.scss"
import axios from 'axios'
import AppContext from '../../context'
import {Link} from "react-router-dom"


const DeliveryForm = () => {
    const {cartItems} = useContext(AppContext) 
    
    const [errorName, setErrorName] = useState(false)
    const [errorCity, setErrorCity] = useState(false)
    const [errorPhone, setErrorPhone] = useState(false)
    const [errorAdress, setErrorAdress] = useState(false)
    const {initialState} = {
        "name":"",
        "city":"",
        "adress":"",
        "phone":"",
        
    }
    const [formValue, setFormValue] = useState(initialState)

    const postData = async(obj)=>{
        try{
            await axios.post("https://62f615b0612c13062b45e6f7.mockapi.io/personData", obj)
        }
        catch(err){
            console.log(err.message)
        }
    }

    const onInputChange = (e)=>{
        // console.log(e.target.name, e.target.value)
        const {name, value} = e.target
        setFormValue({...formValue, [name]:value})


    }
    console.log(formValue)

    const checkEmptyInputs = ()=>{

    }

    

    const getDataFromInput = (e) =>{
        e.preventDefault()

        const {name, city, adress, phone} = formValue;
        const obj = {...formValue, items:cartItems}
        console.log(obj)
        console.log(name, city, adress, phone)
        console.elog(errorName)
        if(!errorName && !errorCity && !errorAdress && !errorPhone){
            postData(obj)
        }
        
        //Your order is accepted

    }

  return (
    <form onSubmit={()=>getDataFromInput()}>
        <div className={styles.inputs}>
            <div className={styles.input}>
                <p>Name</p>   
                <input name="name" placeholder="Enter your name..." onChange={onInputChange}></input>
            </div>
            <div className={styles.input}>
                <p>City</p>   
                <input name="city" placeholder="Enter your city..." onChange={onInputChange}></input>
            </div>
            <div className={styles.input}>
                <p>Adress</p>   
                <input name="adress" placeholder="Enter your adress..." onChange={onInputChange}></input>
            </div>
            <div className={styles.input}>
                <p>Phone</p>   
                <input name="phone" placeholder="Enter your phone..." onChange={onInputChange}></input>
            </div>
            <Link to="/accept">
                <button className={styles.submit} type="submit">Submit</button>
            </Link>
            
        </div>
    </form>
    
  )
}

export default DeliveryForm
